package run.stmt.ex;

public class RunEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 1; //변수 x를 선언하고 1을 저장
		int y = 2; //변수 y를 선언하고 2를 저장
		int result = x + y;//x와 y를 더함
		System.out.println(x+"+"+y+"="+ result);
		//콘솔에 출력하는 메소드 호출
	}

}
